import{g as t,h as e,o as a,j as d}from"./chunks/framework.BOQ8FqoK.js";const g=JSON.parse('{"title":"计算机中数据的表示","description":"","frontmatter":{"title":"计算机中数据的表示","date":"2020-12-31T15:38:28.000Z","updated":"2020-12-31T15:38:28.000Z","published":false,"categories":["Computer Science","Computer Organization"],"tags":["Computer Organization"]},"headers":[{"level":1,"title":"计算机中数据的表示","slug":"计算机中数据的表示","link":"#计算机中数据的表示","children":[{"level":2,"title":"基本概念","slug":"基本概念","link":"#基本概念","children":[]},{"level":2,"title":"浮点数","slug":"浮点数","link":"#浮点数","children":[{"level":3,"title":"IEEE 754","slug":"ieee-754","link":"#ieee-754","children":[]},{"level":3,"title":"特殊值","slug":"特殊值","link":"#特殊值","children":[]}]}]}],"relativePath":"posts/2020/12/31/binary-system.md","filePath":"posts/2020/12/31/binary-system.md"}'),r={name:"posts/2020/12/31/binary-system.md"},l=d('<h1 id="计算机中数据的表示" tabindex="-1">计算机中数据的表示 <a class="header-anchor" href="#计算机中数据的表示" aria-label="Permalink to &quot;计算机中数据的表示&quot;">​</a></h1><h2 id="基本概念" tabindex="-1">基本概念 <a class="header-anchor" href="#基本概念" aria-label="Permalink to &quot;基本概念&quot;">​</a></h2><p><strong>真值</strong>：带符号的数字：-1、-2、+2</p><p><strong>原码</strong>：最高位表示符号，0表示正、1表示负，其中0,000和1,0...0都表示数字0（逗号前为符号），表示范围在数轴上关于原点对称</p><p><strong>反码</strong>：除符号位外取反，如 1,010101 （原）-&gt; 1,101010（反），其中依然有两个零，表示范围关于原点对称</p><p><strong>补码</strong>：反码+1得补码，如 1,010101 （原）-&gt; 1,101011（移），其中零只有一个，因此表示得负数比正数多一个（全0表示-2^n）</p><p><strong>移码</strong>：移码是表示范围在在数轴上偏移，移码大，真值就大，移码常常用来表示浮点数的阶码，加快比较。</p><h2 id="浮点数" tabindex="-1">浮点数 <a class="header-anchor" href="#浮点数" aria-label="Permalink to &quot;浮点数&quot;">​</a></h2><h3 id="ieee-754" tabindex="-1">IEEE 754 <a class="header-anchor" href="#ieee-754" aria-label="Permalink to &quot;IEEE 754&quot;">​</a></h3><table><thead><tr><th>名称</th><th>尾数符号位</th><th>尾数</th><th>阶码</th><th>阶码偏移值</th><th>隐藏位</th></tr></thead><tbody><tr><td>短浮点数</td><td>1</td><td>23</td><td>7</td><td>2^7-1</td><td>Yes</td></tr><tr><td>长浮点数</td><td>1</td><td>52</td><td>11</td><td>2^10-1</td><td>Yes</td></tr><tr><td>临时实数</td><td>1</td><td>64</td><td>15</td><td>2^14-1</td><td>No</td></tr></tbody></table><h3 id="特殊值" tabindex="-1">特殊值 <a class="header-anchor" href="#特殊值" aria-label="Permalink to &quot;特殊值&quot;">​</a></h3><ul><li>尾数为零且阶码为零表示零</li><li>尾数为零且阶码全一表示无穷大</li><li>尾数非零且阶码全一表示这不是一个数（NaN）</li></ul>',12),i=[l];function s(o,n,h,c,p,u){return a(),e("div",null,i)}const m=t(r,[["render",s]]);export{g as __pageData,m as default};
