import{g as r,h as i,k as e,l as a,j as o,o as l}from"./chunks/framework.BOQ8FqoK.js";const s="/assets/gap-between-neuron-and-function.CLTuytVJ.png",c="/assets/development-neurons.ErkXU_my.png",p="/assets/neuron-translocation-optical.KMPRDdxy.png",h="/assets/NECs.lP8mbFAv.png",t="/assets/RGCs.C9Bk3Xxt.png",d="/assets/radial-unit-hypothesis.BgGox_OJ.png",u="/assets/IPs.Dkcdcana.png",n="/assets/bRGCs.Bbc3Huzn.png",m="/assets/neuron-translocation.CrsZ1MPM.png",g="/assets/birthdate-dependent-inside-out.CDTDZFiy.png",T="/assets/neuron-consecutive-waves.ueRmIrmb.png",_="/assets/ganglionic-eminence.CVMTxIk3.png",b="/assets/inhibition-neuron-location-and-translocation.CuSt4cj2.png",f="/assets/inhibition-neuron-translocation.b8k3nEWH.png",k="/assets/inhibition-neuron-translocation-path.BpMqZOHH.png",Q="/assets/inhibition-neuron-destiny.rThus_bU.png",x="/assets/gliocyte-generation.Dd7zmqN2.png",q="/assets/gliocyte-generation-ratio.EpQ63vKh.png",K=JSON.parse('{"title":"神经元的生成","description":"","frontmatter":{"date":"2024-02-14T00:00:00.000Z","updated":"2024-02-15T00:00:00.000Z","author":"Zexin Yuan","location":"Guan Ze, Fu Jian, China","tags":["Course","Neuron Science 2023"],"categories":["Neuron Science"]},"headers":[{"level":1,"title":"神经元的生成","slug":"神经元的生成","link":"#神经元的生成","children":[{"level":2,"title":"大脑皮层发育是一个高度调控的过程","slug":"大脑皮层发育是一个高度调控的过程","link":"#大脑皮层发育是一个高度调控的过程","children":[]},{"level":2,"title":"神经干细胞","slug":"神经干细胞","link":"#神经干细胞","children":[{"level":3,"title":"神经上皮细胞","slug":"神经上皮细胞","link":"#神经上皮细胞","children":[]},{"level":3,"title":"放射状胶质细胞","slug":"放射状胶质细胞","link":"#放射状胶质细胞","children":[]},{"level":3,"title":"中间前体细胞","slug":"中间前体细胞","link":"#中间前体细胞","children":[]},{"level":3,"title":"基底放射状胶质细胞","slug":"基底放射状胶质细胞","link":"#基底放射状胶质细胞","children":[]}]},{"level":2,"title":"大脑皮层的进化","slug":"大脑皮层的进化","link":"#大脑皮层的进化","children":[{"level":3,"title":"为什么人类拥有更高等智力？","slug":"为什么人类拥有更高等智力","link":"#为什么人类拥有更高等智力","children":[]},{"level":3,"title":"大脑沟回产生促进更高智力的形成","slug":"大脑沟回产生促进更高智力的形成","link":"#大脑沟回产生促进更高智力的形成","children":[]}]},{"level":2,"title":"兴奋性神经元产生","slug":"兴奋性神经元产生","link":"#兴奋性神经元产生","children":[{"level":3,"title":"神经元迁移","slug":"神经元迁移","link":"#神经元迁移","children":[]},{"level":3,"title":"神经元命运","slug":"神经元命运","link":"#神经元命运","children":[]}]},{"level":2,"title":"抑制性神经元的产生与迁移","slug":"抑制性神经元的产生与迁移","link":"#抑制性神经元的产生与迁移","children":[{"level":3,"title":"抑制性神经元的产生","slug":"抑制性神经元的产生","link":"#抑制性神经元的产生","children":[]},{"level":3,"title":"抑制性神经元的迁移","slug":"抑制性神经元的迁移","link":"#抑制性神经元的迁移","children":[]},{"level":3,"title":"抑制性神经元的命运","slug":"抑制性神经元的命运","link":"#抑制性神经元的命运","children":[]}]},{"level":2,"title":"神经胶质细胞的产生","slug":"神经胶质细胞的产生","link":"#神经胶质细胞的产生","children":[]},{"level":2,"title":"References","slug":"references","link":"#references","children":[]}]}],"relativePath":"posts/2024/02/14/neurogenesis-1.md","filePath":"posts/2024/02/14/neurogenesis-1.md"}'),P={name:"posts/2024/02/14/neurogenesis-1.md"},C=e("h1",{id:"神经元的生成",tabindex:"-1"},[a("神经元的生成 "),e("a",{class:"header-anchor",href:"#神经元的生成","aria-label":'Permalink to "神经元的生成"'},"​")],-1),v=e("p",null,"神经科学第五讲，讲者：禹永春（复旦大学），主持人：朱筱娟教授（东北师范大学）。",-1),R={class:"MathJax",jax:"SVG",style:{direction:"ltr",position:"relative"}},G={style:{overflow:"visible","min-height":"1px","min-width":"1px","vertical-align":"-0.025ex"},xmlns:"http://www.w3.org/2000/svg",width:"3.954ex",height:"1.912ex",role:"img",focusable:"false",viewBox:"0 -833.9 1747.6 844.9","aria-hidden":"true"},S=o('<g stroke="currentColor" fill="currentColor" stroke-width="0" transform="scale(1,-1)"><g data-mml-node="math"><g data-mml-node="mi"><path data-c="1D450" d="M34 159Q34 268 120 355T306 442Q362 442 394 418T427 355Q427 326 408 306T360 285Q341 285 330 295T319 325T330 359T352 380T366 386H367Q367 388 361 392T340 400T306 404Q276 404 249 390Q228 381 206 359Q162 315 142 235T121 119Q121 73 147 50Q169 26 205 26H209Q321 26 394 111Q403 121 406 121Q410 121 419 112T429 98T420 83T391 55T346 25T282 0T202 -11Q127 -11 81 37T34 159Z" style="stroke-width:3;"></path></g><g data-mml-node="msup" transform="translate(433,0)"><g data-mml-node="mi"><path data-c="1D45A" d="M21 287Q22 293 24 303T36 341T56 388T88 425T132 442T175 435T205 417T221 395T229 376L231 369Q231 367 232 367L243 378Q303 442 384 442Q401 442 415 440T441 433T460 423T475 411T485 398T493 385T497 373T500 364T502 357L510 367Q573 442 659 442Q713 442 746 415T780 336Q780 285 742 178T704 50Q705 36 709 31T724 26Q752 26 776 56T815 138Q818 149 821 151T837 153Q857 153 857 145Q857 144 853 130Q845 101 831 73T785 17T716 -10Q669 -10 648 17T627 73Q627 92 663 193T700 345Q700 404 656 404H651Q565 404 506 303L499 291L466 157Q433 26 428 16Q415 -11 385 -11Q372 -11 364 -4T353 8T350 18Q350 29 384 161L420 307Q423 322 423 345Q423 404 379 404H374Q288 404 229 303L222 291L189 157Q156 26 151 16Q138 -11 108 -11Q95 -11 87 -5T76 7T74 17Q74 30 112 181Q151 335 151 342Q154 357 154 369Q154 405 129 405Q107 405 92 377T69 316T57 280Q55 278 41 278H27Q21 284 21 287Z" style="stroke-width:3;"></path></g><g data-mml-node="mn" transform="translate(911,363) scale(0.707)"><path data-c="32" d="M109 429Q82 429 66 447T50 491Q50 562 103 614T235 666Q326 666 387 610T449 465Q449 422 429 383T381 315T301 241Q265 210 201 149L142 93L218 92Q375 92 385 97Q392 99 409 186V189H449V186Q448 183 436 95T421 3V0H50V19V31Q50 38 56 46T86 81Q115 113 136 137Q145 147 170 174T204 211T233 244T261 278T284 308T305 340T320 369T333 401T340 431T343 464Q343 527 309 573T212 619Q179 619 154 602T119 569T109 550Q109 549 114 549Q132 549 151 535T170 489Q170 464 154 447T109 429Z" style="stroke-width:3;"></path></g></g></g></g>',1),y=[S],V=e("mjx-assistive-mml",{unselectable:"on",display:"inline",style:{top:"0px",left:"0px",clip:"rect(1px, 1px, 1px, 1px)","-webkit-touch-callout":"none","-webkit-user-select":"none","-khtml-user-select":"none","-moz-user-select":"none","-ms-user-select":"none","user-select":"none",position:"absolute",padding:"1px 0px 0px 0px",border:"0px",display:"block",width:"auto",overflow:"hidden"}},[e("math",{xmlns:"http://www.w3.org/1998/Math/MathML"},[e("mi",null,"c"),e("msup",null,[e("mi",null,"m"),e("mn",null,"2")])])],-1),w=o('<p><img src="'+s+'" alt="脑早期发育到脑功能之间仍存在需要探索的鸿沟"></p><p>上一讲：<a href="./../11/development-of-nerve-system.html">神经系统的发生与分化</a>，下一讲：<a href="./../17/neurogenesis-2.html">神经元的发育</a></p><h2 id="大脑皮层发育是一个高度调控的过程" tabindex="-1">大脑皮层发育是一个高度调控的过程 <a class="header-anchor" href="#大脑皮层发育是一个高度调控的过程" aria-label="Permalink to &quot;大脑皮层发育是一个高度调控的过程&quot;">​</a></h2><p>在适当的时间产生相应数量和类型的神经细胞，并迁移到适当的位置，逐步分化成熟形成复杂而精确的局部和远程神经连接。</p><p><img src="'+c+'" alt="干细胞在不同阶段的分裂过程 "></p><p>对一个早期发育中的脑切片，可以看见脑室区（Ventricular zone, VZ）、中间带（Intermediate zone, IZ）和皮层板（Cortical plate, CP），脑室区中存在大量神经干细胞，它们分裂产生不成熟的神经元，这些神经元随之向皮层板迁移。</p><p><img src="'+p+'" alt="神经元产生与迁移"></p><h2 id="神经干细胞" tabindex="-1">神经干细胞 <a class="header-anchor" href="#神经干细胞" aria-label="Permalink to &quot;神经干细胞&quot;">​</a></h2><h3 id="神经上皮细胞" tabindex="-1">神经上皮细胞 <a class="header-anchor" href="#神经上皮细胞" aria-label="Permalink to &quot;神经上皮细胞&quot;">​</a></h3><p>神经上皮细胞（Neuroepithelial cells, NECs），特征：</p><ol><li>出现在发育早期（小鼠E9-11），不产生神经元；</li><li>高度极化；</li><li>增殖的对称分裂（分裂后产生相似的细胞），区间动态核迁移增殖；</li><li>扩充神经前体细胞池。</li></ol><p><img src="'+h+'" alt="神经上皮细胞"></p><h3 id="放射状胶质细胞" tabindex="-1">放射状胶质细胞 <a class="header-anchor" href="#放射状胶质细胞" aria-label="Permalink to &quot;放射状胶质细胞&quot;">​</a></h3><p>放射状胶质细胞（Radial glial cells, RGCs），特征：</p><ol><li>负责产生皮层几乎所有的神经元和神经胶质细胞；</li><li>高度极化：“顶天立地”；</li><li>仍保留了一部分NECs的特性，如还会进行对称分裂；</li><li>不对称分裂：直接产生神经元或其他神经前体细胞；</li><li>顶端纤维：神经元迁移的“脚手架”；</li><li>具有高度区域特异性：产生区域特异性（脑区）的细胞和数量；</li><li>发育晚期，少部分RGCs进入到神经胶质细胞发生阶段，产生神经胶质细胞。RGCs逐渐消失，只有少部分转化为成体神经干细胞和室管膜细胞。</li></ol><p><img src="'+t+'" alt="放射状胶质细胞"></p><ol><li>RGCs在有丝分裂周期中，细胞核沿着细胞的顶-基轴径向移动，该过程称为<strong>区间动态核迁移</strong>。</li><li>在M期，细胞核迁移到VZ表面进行细胞质分裂。</li><li>形成典型的假复层（pseudostratified）结构，有助于在有限的空间内容纳更多的神经干细胞。</li></ol><p><img src="'+t+'" alt="放射状胶质细胞的区间动态核迁移"></p><h4 id="放射单元假说" tabindex="-1">放射单元假说 <a class="header-anchor" href="#放射单元假说" aria-label="Permalink to &quot;放射单元假说&quot;">​</a></h4><p>放射单元假说（radial unit hypothesis）认为在皮层发育过程中，由克隆相关神经细胞沿着相同放射状胶质纤维的迁移形成的个体发育，是成体皮层的功能性基础元件。这一假说的结构与皮层功能柱存在相似，但二者的关联尚未完全清晰。</p><p><img src="'+d+'" alt="放射单元假说"></p><h3 id="中间前体细胞" tabindex="-1">中间前体细胞 <a class="header-anchor" href="#中间前体细胞" aria-label="Permalink to &quot;中间前体细胞&quot;">​</a></h3><p>中间前体细胞 （Intermediate progenitors, IPs），特征：</p><ol><li>由RGCs非对称分裂产生；</li><li>主要存在于SVZ（表达Tbr2）；</li><li>间接神经发生；</li><li>主要呈双极（vIP）和多极（olP）形态；</li><li>贡献皮层~80%神经元（啮齿动物）；</li><li>啮齿类动物大部分IPs只进行一次对称分裂产生两个神经元，而灵长类动物IPs可进行多次对称分裂并产生更多的神经元。</li></ol><p><img src="'+u+'" alt="中间前体细胞"></p><h3 id="基底放射状胶质细胞" tabindex="-1">基底放射状胶质细胞 <a class="header-anchor" href="#基底放射状胶质细胞" aria-label="Permalink to &quot;基底放射状胶质细胞&quot;">​</a></h3><p>基底放射状胶质细胞 （basal radial glial cells, bRGCs），特征：</p><ol><li>由RGCs非对称分裂产生；</li><li>间接神经发生；</li><li>主要存在于OSVZ；</li><li>可增殖性对称分裂；</li><li>主要呈单极形态；</li><li>可间接产生神经元或IPS；</li><li>具有沟回皮层结构动物（包括雪貂、灵长类动物等）产生神经元的主要方式。啮齿类动物有极少的bRGCs。</li></ol><p><img src="'+n+'" alt="基底放射状胶质细胞"></p><p>bRGCs 细胞将 SVZ 分为 OSVZ 和 ISVZ 两个区域。</p><p><img src="'+n+'" alt="基底放射状胶质细胞"></p><h2 id="大脑皮层的进化" tabindex="-1">大脑皮层的进化 <a class="header-anchor" href="#大脑皮层的进化" aria-label="Permalink to &quot;大脑皮层的进化&quot;">​</a></h2><h3 id="为什么人类拥有更高等智力" tabindex="-1">为什么人类拥有更高等智力？ <a class="header-anchor" href="#为什么人类拥有更高等智力" aria-label="Permalink to &quot;为什么人类拥有更高等智力？&quot;">​</a></h3><p>大脑的容量大不等于聪明，例如大象没有人聪明。更可能的是直接与神经元的数量相关，据实验估计小鼠约 7000 万个神经元，人类约 860 亿个神经元。</p><p>大脑重量仅为体重的~2%。却消耗了身体25%的能量：500kCal/天，即10亿神经元=6kCal/天！如果小鼠拥有和人一样多的神经细胞（~860亿），那么小鼠的大脑重量将达到36Kg！那么进化的一个问题就是如何在有限的颅腔里容纳更多的神经细胞以及摄取更多的能量！</p><p>假设非人灵长类动物平均每天进食8个小时，那么据推算 530 亿神经元的动物可以到 25Kg 体重，450 亿神经元的动物可以达到 50Kg 体重，300亿神经元的动物最大可以达到 75Kg 体重，120 亿神经元的动物可以达到 100Kg 体重，而人类 860 亿神经元却可以达到 75Kg 的体重。</p><p>人类在进化过程中通过大规模劳动获取更多的能量，因此人类进化的本质是大脑的进化。</p><h3 id="大脑沟回产生促进更高智力的形成" tabindex="-1">大脑沟回产生促进更高智力的形成 <a class="header-anchor" href="#大脑沟回产生促进更高智力的形成" aria-label="Permalink to &quot;大脑沟回产生促进更高智力的形成&quot;">​</a></h3><p>大脑皮层沟回形成是逻辑、思维、认知、情感、精细运动、语言、意识等脑高级功能基础。在进化上具有极其重要的意义。</p><p>如果出现无脑回/巨脑回畸形，会导致智力低下、癫痫等。这是一类遗传疾病，诊断简单（MRI），存在高风险治病基因，但仍然大量治病基因目前不清楚。</p><p>沟回结构在进化分支中独立出现，大脑沟回结构具有保守性和差异性。一级沟回 （Primary folds），物种间保守。二级沟回（Secondary folds），物种间存在差异，物种内保守。三级沟回（Tertiary folds），物种内个体间存在差异。</p><h4 id="沟回的形成" tabindex="-1">沟回的形成 <a class="header-anchor" href="#沟回的形成" aria-label="Permalink to &quot;沟回的形成&quot;">​</a></h4><h5 id="牵张力学说与皮层扩张学说" tabindex="-1">牵张力学说与皮层扩张学说 <a class="header-anchor" href="#牵张力学说与皮层扩张学说" aria-label="Permalink to &quot;牵张力学说与皮层扩张学说&quot;">​</a></h5><p>早期有科学家猜测沟回形成是由于生长过程中的轴突形成后张力纤维在不同区域间的产生不同的牵张力形成的。但是经过外科医生实验，在切断张力纤维后，沟回结构仍然可以形成，因此牵张力不是沟回形成的唯一的原因。</p><p>另一种说法是皮质扩张机制，即在有限容积内，皮质的扩张必然导致折叠。但是在外科实验中，在去除颅骨束缚后，仍会产生沟回结构。</p><h5 id="皮层沟回发育的细胞学机制" tabindex="-1">皮层沟回发育的细胞学机制 <a class="header-anchor" href="#皮层沟回发育的细胞学机制" aria-label="Permalink to &quot;皮层沟回发育的细胞学机制&quot;">​</a></h5><p>人类皮层有沟回，而小鼠没有沟回，二者差距主要有三个方面：</p><ul><li>发育时间：小鼠妊娠18天，人妊娠40周；</li><li>干细胞类型：灵长类特异存在OSVZ（oRGs）；</li><li>神经元迁移：小鼠垂直径向迁移，灵长类径向发散迁移。</li></ul><h5 id="基因调控机制" tabindex="-1">基因调控机制 <a class="header-anchor" href="#基因调控机制" aria-label="Permalink to &quot;基因调控机制&quot;">​</a></h5><p>沟回脑具有皮质折叠的遗传基因地图（The genetic protomaps），在皮层沟回形成之前以模块模式表达，调控沟回形成。</p><h2 id="兴奋性神经元产生" tabindex="-1">兴奋性神经元产生 <a class="header-anchor" href="#兴奋性神经元产生" aria-label="Permalink to &quot;兴奋性神经元产生&quot;">​</a></h2><h3 id="神经元迁移" tabindex="-1">神经元迁移 <a class="header-anchor" href="#神经元迁移" aria-label="Permalink to &quot;神经元迁移&quot;">​</a></h3><p>兴奋性神经元迁移主要有三种形式：早期主要依赖于细胞体移动，中期主要通过胶质细胞的顶端突起介导移动（主要是分化该神经元的干细胞），还有通过依赖顶端突起与软脑膜的牵拉力的终端胞体移动。</p><p><img src="'+m+'" alt="兴奋性神经元迁移的三种主要形式"></p><p>神经元的迁移与定位遵循“出生时间依赖性的由内到外”的规则(birthdate-dependent inside-out)</p><p><img src="'+g+'" alt="出生时间依赖性的由内到外"></p><h3 id="神经元命运" tabindex="-1">神经元命运 <a class="header-anchor" href="#神经元命运" aria-label="Permalink to &quot;神经元命运&quot;">​</a></h3><p>神经元的迁移未知</p><p><img src="'+T+'" alt="神经元的命运决定收到精确的时空调控"></p><h4 id="预先决定的命运限制假说" tabindex="-1">预先决定的命运限制假说 <a class="header-anchor" href="#预先决定的命运限制假说" aria-label="Permalink to &quot;预先决定的命运限制假说&quot;">​</a></h4><p>预先决定的命运限制 （pre-determined fate restriction）即神经干细胞具有异质性，不同亚型的神经干细胞产生特定亚型的神经细胞，因此神经细胞的命运在发育早期就已确定。</p><h4 id="命运逐渐受限假说" tabindex="-1">命运逐渐受限假说 <a class="header-anchor" href="#命运逐渐受限假说" aria-label="Permalink to &quot;命运逐渐受限假说&quot;">​</a></h4><p>命运逐渐受限 （temporally progressive fate restriction）即神经干细胞在发育早期具有多能性，但随着发育的进行，其分化潜力逐渐受限，因此在发育的不同时间点产生不同亚型的神经细胞。</p><h2 id="抑制性神经元的产生与迁移" tabindex="-1">抑制性神经元的产生与迁移 <a class="header-anchor" href="#抑制性神经元的产生与迁移" aria-label="Permalink to &quot;抑制性神经元的产生与迁移&quot;">​</a></h2><h3 id="抑制性神经元的产生" tabindex="-1">抑制性神经元的产生 <a class="header-anchor" href="#抑制性神经元的产生" aria-label="Permalink to &quot;抑制性神经元的产生&quot;">​</a></h3><p>对大脑做水平面切片，可以看见腹侧有三个隆起区域，</p><p><img src="'+_+'" alt="腹侧隆起"></p><p>抑制性（中间）神经元主要从抑制性神经元的起源内侧神经节隆起（medial ganglionic eminence, MGE）、尾侧神经节隆起（caudal ganglionic eminence, CGE）、视前区（preoptic area, PoA）产生。而外侧神经节隆起（lateral ganglionic eminence, LGE）主要是嗅球中间神经元及纹状体投射神经元。</p><h4 id="腹侧隆起区的神经前体细胞" tabindex="-1">腹侧隆起区的神经前体细胞 <a class="header-anchor" href="#腹侧隆起区的神经前体细胞" aria-label="Permalink to &quot;腹侧隆起区的神经前体细胞&quot;">​</a></h4><p>腹侧隆起区的神经前体细胞，特征：</p><ol><li>存在大量bRGCS；</li><li>随着发育，aRGCs数量显著减少，SVZ则显著扩增，包含大量的IPs；</li><li>IPs可进行多次对称分裂；</li><li>存在两类aRGCs；</li><li>随着发育，投射至软脑膜表面的aRGCs数量减少，血管锚定的RGCs数量增多。</li></ol><h3 id="抑制性神经元的迁移" tabindex="-1">抑制性神经元的迁移 <a class="header-anchor" href="#抑制性神经元的迁移" aria-label="Permalink to &quot;抑制性神经元的迁移&quot;">​</a></h3><p>与兴奋性神经元在皮层产生不同，抑制性神经元的产生位置在腹侧隆起产生，多数需要经过一段较长的迁移后到达皮层。</p><p><img src="'+b+'" alt="抑制性神经需要经过较长的迁移"></p><p>抑制性神经元的迁移大致分为四步，但其中许多细节仍未清楚。</p><p><img src="'+f+'" alt="抑制性神经迁移过程"></p><p>抑制性神经元在迁移过程中会形成不同的迁移流，主要是由于铆钉在软脑膜的干细胞生成的中间神经元会沿着边缘区水平迁移，而锚定在血管上的干细胞生成的神经元则会沿着侧脑室区（SVZ）迁移。切向迁移路线的选择与中间神经元的起源地、出生时间和类型等相关。小鼠大脑皮层中的中间神经元在出生后两周内有大约30-40%发生凋亡。</p><p><img src="'+k+'" alt="抑制性神经迁移路径"></p><h3 id="抑制性神经元的命运" tabindex="-1">抑制性神经元的命运 <a class="header-anchor" href="#抑制性神经元的命运" aria-label="Permalink to &quot;抑制性神经元的命运&quot;">​</a></h3><p><img src="'+Q+'" alt="抑制性神经元的命运决定受到精确的时空调控"></p><h2 id="神经胶质细胞的产生" tabindex="-1">神经胶质细胞的产生 <a class="header-anchor" href="#神经胶质细胞的产生" aria-label="Permalink to &quot;神经胶质细胞的产生&quot;">​</a></h2><p>在神经细胞生成后，大脑会进一步分化产生神经胶质细胞。</p><p><img src="'+x+'" alt="神经胶质细胞的产生"></p><p>神经胶质细胞的分化比例：</p><p><img src="'+q+'" alt="神经胶质细胞的分化比例"></p><p>少突胶质细胞（oligodendrocytes, OLs）呈现三波产生的时空特征，第一波从 MGE/PoA(E12.5)产生，第二波从 LGE/CGE(E15.5) 产生，第三波从背侧 RGCs(E16.5)产生。值得注意的是，第三波产生时第一波产生的OLs大量凋亡，因此在成年时的 OLs 主要来源于第二和第三波，并且保留大量具有分裂能力少突胶质细胞的前体细胞（OPCS）。</p><h2 id="references" tabindex="-1">References <a class="header-anchor" href="#references" aria-label="Permalink to &quot;References&quot;">​</a></h2><ol><li>《神经科学》韩济生主编（第4版）</li><li>&quot;Principles of Neural Science&quot;, E.R. Kandel主编（第六版）</li><li>«Principles of Neurobiology» ,Liqun Luo主编（第二版）</li><li>《现代医学神经生物学》，段树民/徐天乐主编</li><li>Kohwi M, Doe CQ. Temporal fate specification and neural progenitor competence during development. Nat Rev Neurosci. 2013, 14(12): 823-38.</li><li>Wurst W, Bally-Cuif L. Neural plate patterning: upstream and downstream of the isthmic organizer. Nat Rev Neurosci. 2001, 2(2): 99-108.</li><li>Gao P, Postiglione MPet al. Deterministic progenitor behavior and unitary production of neurons in the neocortex. Cell. 2014, 159(4): 775-88.</li><li>Wang X, Tsai JW, et al. Asymmetric centrosome inheritance maintains neural progenitors in the neocortex. Nature. 2009, 461(7266): 947-55.</li><li>Shao W, Yang J, He M, et al. Centrosome anchoring regulates progenitor properties and cortical formation. Nature. 2020, 580(7801): 106-12.</li><li>Shen Z, Lin Y, et al. Distinct progenitor behavior underlying neocortical gliogenesis related to tumorigenesis. Cell Rep. 2021, 34(11): 108853.</li><li>Koo B, Lee KH, Ming GL, et al. Setting the clock of neural progenitor cells during mammalian corticogenesis. Semin Cell Dev Biol. 2023, 142: 43-53.</li><li>Lui JH, Hansen DV, Kriegstein AR. Development and evolution of the human neocortex. Cell, 2011, 146: 18-36.</li></ol>',88);function L(N,Z,E,M,I,D){return l(),i("div",null,[C,v,e("p",null,[a("大脑皮层 ~2500"),e("mjx-container",R,[(l(),i("svg",G,y)),V]),a(" 的面积下，约有 160 亿个神经元，形成了高度有序化的结构（ 六层结构、皮质功能柱、皮层区域化），其中约有 80% 兴奋性神经元、20% 中间神经元以及多种类的胶质细胞，神经元/胶质细胞是如何精确发育形成神经环路来执行我们的感知、运动、语言、情感、认知等脑功能仍是一个需要进一步探索的问题。")]),w])}const A=r(P,[["render",L]]);export{K as __pageData,A as default};
